package compose

import (
	"context"
	"fmt"
	nhostssl "github.com/nhost/cli/internal/ssl"
	"io"
	"os"
	"os/exec"
	"path/filepath"

	"github.com/nhost/cli/nhost"
	"github.com/nhost/cli/util"
	"github.com/pkg/errors"
)

const traefikConfig = `
# v1
# DO NOT EDIT THIS FILE
tls:
  certificates:
    - certFile: /opt/traefik/certs/cert.crt
      keyFile: /opt/traefik/certs/cert.key
log:
  level: DEBUG
accessLog: {}
`

type DataStreams struct {
	Stdout io.Writer
	Stderr io.Writer
}

type Wrapper struct {
	workdir            string
	composeProjectName string
}

func InitWrapper(workdir string, cert *nhostssl.SSLCert, gitBranch string, conf *Config) (*Wrapper, error) {
	w := &Wrapper{
		workdir:            workdir,
		composeProjectName: conf.composeProjectName,
	}

	if err := w.init(workdir, cert, gitBranch, conf); err != nil {
		return nil, err
	}

	return w, nil
}

func (w Wrapper) init(workdir string, cert *nhostssl.SSLCert, gitBranch string, conf *Config) error {
	if err := w.ensureFoldersExistForDockerVolumes(workdir, gitBranch); err != nil {
		return err
	}

	if err := w.ensureSSLCertificateExist(workdir, cert); err != nil {
		return err
	}

	if err := w.ensureTraefikConfigFileExists(workdir); err != nil {
		return err
	}

	yamlConf, err := conf.BuildYAML()

	// write data to a docker-compose.yaml file
	err = os.WriteFile(w.dockerComposePath(), yamlConf, 0600)
	if err != nil {
		return errors.Wrap(err, "could not write docker-compose.yaml file")
	}

	return nil
}

func (w Wrapper) ensureTraefikConfigFileExists(workdir string) error {
	traefikConfigFile := filepath.Join(workdir, ".nhost/traefik/traefik.yaml")

	if !util.PathExists(traefikConfigFile) {
		err := os.WriteFile(traefikConfigFile, []byte(traefikConfig), 0777)
		if err != nil {
			return errors.Wrap(err, "could not write traefik configuration file")
		}
	}

	return nil
}

func (w Wrapper) ensureSSLCertificateExist(workdir string, cert *nhostssl.SSLCert) error {
	certsFolder := filepath.Join(workdir, ".nhost/traefik/certs")
	certFile := filepath.Join(certsFolder, "cert.crt")
	keyFile := filepath.Join(certsFolder, "cert.key")

	if err := os.MkdirAll(certsFolder, 0700); err != nil {
		return errors.Wrap(err, fmt.Sprintf("failed to create a folder for SSL certificates '%s'", certsFolder))
	}

	if !util.PathExists(certFile) {
		if err := os.WriteFile(certFile, cert.CertFile, 0600); err != nil {
			return errors.Wrap(err, fmt.Sprintf("failed to write SSL certificate file '%s'", certFile))
		}
	}

	if !util.PathExists(keyFile) {
		if err := os.WriteFile(keyFile, cert.KeyFile, 0600); err != nil {
			return errors.Wrap(err, fmt.Sprintf("failed to write SSL certificate key file '%s'", keyFile))
		}
	}

	return nil
}

func (w Wrapper) dockerComposePath() string {
	return filepath.Join(w.workdir, ".nhost/docker-compose.yaml")
}

func (w Wrapper) ensureFoldersExistForDockerVolumes(workdir, gitBranch string) error {
	dotNhostFolder := filepath.Join(workdir, ".nhost")

	paths := []string{
		filepath.Join(workdir, ".nhost/custom/keys"),
		filepath.Join(dotNhostFolder, MinioDataDirGitBranchScopedPath(gitBranch)),
		filepath.Join(dotNhostFolder, MailHogDataDirGiBranchScopedPath(gitBranch)),
		filepath.Join(dotNhostFolder, DbDataDirGitBranchScopedPath(gitBranch, dataDirPgdata)),
	}

	for _, folder := range paths {
		if err := os.MkdirAll(folder, os.ModePerm); err != nil {
			return errors.Wrap(err, fmt.Sprintf("failed to create data folder '%s'", folder))
		}
	}

	// write pg_hba_local.conf
	hbaFile := filepath.Join(dotNhostFolder, DbDataDirGitBranchScopedPath(gitBranch, "pg_hba_local.conf"))

	err := os.WriteFile(hbaFile, []byte("local all all trust\nhost all all all trust"), 0777)
	if err != nil {
		return errors.Wrap(err, "could not write pg_hba_local.conf")
	}

	return nil
}

func (w Wrapper) Command(ctx context.Context, args []string, streams *DataStreams) (*exec.Cmd, error) {
	dc := exec.CommandContext(ctx, "docker", append([]string{"compose", "-p", w.composeProjectName, "-f", w.dockerComposePath()}, args...)...)

	if streams != nil {
		// set streams
		dc.Stdout = streams.Stdout
		dc.Stderr = streams.Stderr
		dc.Stdin = os.Stdin
	}

	return dc, nil
}

func CommandWithExistingConfig(ctx context.Context, projectName string, args []string, streams *DataStreams) (*exec.Cmd, error) {
	configFilename := filepath.Join(nhost.DOT_NHOST_DIR, "docker-compose.yaml")

	if !util.PathExists(configFilename) {
		return nil, fmt.Errorf("The project hasn't been initialized yet. Please run 'nhost dev' first.")
	}

	dc := exec.CommandContext(ctx, "docker", append([]string{"compose", "-p", projectName, "-f", configFilename}, args...)...)

	if streams != nil {
		// set streams
		dc.Stdout = streams.Stdout
		dc.Stderr = streams.Stderr
		dc.Stdin = os.Stdin
	}

	return dc, nil
}
